/**
 * @brief Renvoie le pointeur de l'animation voulue
 * @param animation recherchee
 * @param facingRight orientation du personnage
 * @retval pointeur vers l'image de l'animation
 */
uint16_t * stateMachine_animation(playerStatus_e state, bool facingRight)
{
	static playerStatus_e prev_state;
	uint16_t * anim;
	switch(state)
	{
		case IDLE:
			if(prev_state != state)
				indexAnim = 0;
			if(facingRight)
				anim = idle[0];
			else
				anim = flipImage(idle[0], 25, 30);
			break;

		case RUN:
			if(prev_state != state)
				indexAnim = 0;
			if(facingRight)
				anim = run[indexAnim];
			else
				anim = flipImage(run[indexAnim], 25, 30);
			indexAnim++;
			if(indexAnim > 3)
				indexAnim = 0;
			break;

		case JUMP:
			if(prev_state != state)
				indexAnim = 0;
			if(facingRight)
				anim = jump[indexAnim];
			else
				anim = flipImage(jump[indexAnim], 25, 30);
			indexAnim++;
			if(indexAnim > 3)
				indexAnim = 2;
			break;

		case FALL:
			if(prev_state != state)
				indexAnim = 0;
			if(facingRight)
				anim = fall[indexAnim];
			else
				anim = flipImage(fall[indexAnim], 25, 30);
			indexAnim++;
			if(indexAnim > 5)
				indexAnim = 4;
			break;
		
		case LAND:
			if(prev_state != state)
				indexAnim = 0;
			if(facingRight)
				anim = land[indexAnim];
			else
				anim = flipImage(land[indexAnim], 25, 30);
			indexAnim++;
			if(indexAnim > 6)
				setPlayerStatus(IDLE);
			break;

		case SHOOT:
			anim = idle[0];
			indexAnim = 0;
			break;

		default:
			anim = idle[0];
			indexAnim = 0;
			break;
	}
	prev_state = state;
	return anim;
}
